function addMarker(a,r){positions=makeLocationArray(r);for(var n=0;n<positions.length;n++){var t=new daum.maps.Marker({position:positions[n]});t.setMap(a),t.setDraggable(!1)}}function makeLocationArray(a){for(var r=new Array,n=a.split(";"),t=0;t<n.length;t++)if(n[t].trim()){var i=n[t].split(",");r[t]=new daum.maps.LatLng(i[0],i[1])}return r}