function addMarker(a,b){positions=makeLocationArray(b);for(var c=0;c<positions.length;c++){var d=new google.maps.Marker({position:positions[c]});d.setMap(a),d.setDraggable(!1)}}function makeLocationArray(a){for(var b=new Array,c=a.split(";"),d=0;d<c.length;d++)if(c[d].trim()){var e=c[d].split(",");b[d]=new google.maps.LatLng(e[0],e[1])}return b}